import React, { Component } from "react";
import swal from "sweetalert";
import { Button, TextField, Link, Typography } from "@material-ui/core";
import { withRouter } from "../utils";
import Header from "../Header";
import "../Styles/Login.css"; // Import custom CSS for additional styling
const axios = require("axios");

class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      username: "",
      password: "",
      isGoogleScriptLoaded: false,
    };
  }

  componentDidMount() {
    // Load the Google Identity Services library
    this.loadGoogleScript();
  }

  loadGoogleScript = () => {
    const script = document.createElement("script");
    script.src = "https://accounts.google.com/gsi/client";
    script.async = true;
    script.defer = true;
    script.onload = this.onGoogleScriptLoad;
    document.body.appendChild(script);
  };

  onGoogleScriptLoad = () => {
    this.setState({ isGoogleScriptLoaded: true });
    this.initializeGoogleSignIn();
  };

  initializeGoogleSignIn = () => {
    if (window.google) {
      window.google.accounts.id.initialize({
        client_id:
          "480201724067-6k5cjhkqeg9gpk3aeomrq5vborb8kjqv.apps.googleusercontent.com",
        callback: this.handleCredentialResponse,
      });
      window.google.accounts.id.renderButton(
        document.getElementById("google-signin-button"),
        { theme: "outline", size: "large" }
      );
    }
  };

  handleCredentialResponse = (response) => {
    axios
      .post("http://localhost:2000/google-login", {
        id_token: response.credential,
      })
      .then((res) => {
        // Handle successful login
        localStorage.setItem("token", res.data.token);
        localStorage.setItem("user_id", res.data.id);
        localStorage.setItem("loggedIn", "true");
        this.props.navigate("/HomeDashboard");
      })
      .catch((err) => {
        console.error("Google Sign-In failed:", err);
        swal({
          text: "Google Sign-In failed. Please try again.",
          icon: "error",
        });
      });
  };

  onChange = (e) => this.setState({ [e.target.name]: e.target.value });

  login = () => {
    const { username, password } = this.state;

    axios
      .post("http://localhost:2000/login", {
        username: username,
        password: password,
      })
      .then((res) => {
        localStorage.setItem("token", res.data.token);
        localStorage.setItem("user_id", res.data.id);
        localStorage.setItem("loggedIn", "true");
        this.props.navigate("/HomeDashboard");
      })
      .catch((err) => {
        if (
          err.response &&
          err.response.data &&
          err.response.data.errorMessage
        ) {
          swal({
            text: err.response.data.errorMessage,
            icon: "error",
          });
        }
      });
  };

  render() {
    return (
      <div className="login-container">
        <div className="login-form">
          <Typography variant="h4" className="login-heading">
            Login
          </Typography>
          <TextField
            id="username"
            type="text"
            autoComplete="off"
            name="username"
            value={this.state.username}
            onChange={this.onChange}
            label="Username"
            variant="outlined"
            fullWidth
            className="input-field"
          />
          <TextField
            id="password"
            type="password"
            autoComplete="off"
            name="password"
            value={this.state.password}
            onChange={this.onChange}
            label="Password"
            variant="outlined"
            fullWidth
            className="input-field"
          />
          <Button
            variant="contained"
            color="primary"
            size="large"
            disabled={this.state.username === "" || this.state.password === ""}
            onClick={this.login}
            fullWidth
            className="login-button"
          >
            Login
          </Button>
          <div className="google-signin">
            <div id="google-signin-button"></div>
          </div>
          <Link
            className="register-link"
            component="button"
            onClick={() => {
              this.props.navigate("/register");
            }}
          >
            Register
          </Link>
        </div>
        <Header />
      </div>
    );
  }
}

export default withRouter(Login);
