import React, { Component } from "react";
import swal from "sweetalert";
import { Button, TextField, Link, Typography, Container } from "@material-ui/core";
import { withRouter } from "../utils";
import Header from "../Header";
import GoogleIcon from '@material-ui/icons/Google';
const axios = require("axios");

class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      username: '',
      password: '',
      isGoogleScriptLoaded: false,
    };
  }

  componentDidMount() {
    // Load the Google Identity Services library
    this.loadGoogleScript();
  }

  loadGoogleScript = () => {
    const script = document.createElement('script');
    script.src = 'https://accounts.google.com/gsi/client';
    script.async = true;
    script.defer = true;
    script.onload = this.onGoogleScriptLoad;
    document.body.appendChild(script);
  }

  onGoogleScriptLoad = () => {
    this.setState({ isGoogleScriptLoaded: true });
    this.initializeGoogleSignIn();
  }

  initializeGoogleSignIn = () => {
    if (window.google) {
      window.google.accounts.id.initialize({
        client_id: '480201724067-6k5cjhkqeg9gpk3aeomrq5vborb8kjqv.apps.googleusercontent.com',
        callback: this.handleCredentialResponse,
      });
      window.google.accounts.id.renderButton(
        document.getElementById("google-signin-button"),
        { theme: "outline", size: "large" } // Customization attributes
      );
    }
  }

  handleCredentialResponse = (response) => {
    axios.post('http://localhost:2000/google-login', {
      id_token: response.credential
    }).then(res => {
      // Handle successful login
      localStorage.setItem('token', res.data.token);
      localStorage.setItem('user_id', res.data.id);
      localStorage.setItem('loggedIn', 'true'); // Set flag indicating user is logged in
      this.props.navigate("/HomeDashboard");
    }).catch(err => {
      console.error('Google Sign-In failed:', err);
      swal({
        text: 'Google Sign-In failed. Please try again.',
        icon: "error",
      });
    });
  }

  onChange = (e) => this.setState({ [e.target.name]: e.target.value });

  login = () => {
    const { username, password } = this.state;

    axios.post('http://localhost:2000/login', {
      username: username,
      password: password,
    }).then((res) => {
      localStorage.setItem('token', res.data.token);
      localStorage.setItem('user_id', res.data.id);
      localStorage.setItem('loggedIn', 'true');
      this.props.navigate("/HomeDashboard");
    }).catch((err) => {
      if (err.response && err.response.data && err.response.data.errorMessage) {
        swal({
          text: err.response.data.errorMessage,
          icon: "error",
        });
      }
    });
  }

  render() {
    const backgroundStyle = {
      backgroundImage: 'linear-gradient(to right, #ff7e5f, #feb47b)',
      height: '100vh',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      padding: '20px',
    };

    const formStyle = {
      background: 'rgba(255, 255, 255, 0.95)',
      padding: '40px',
      borderRadius: '15px',
      boxShadow: '0px 8px 20px rgba(0, 0, 0, 0.2)',
      width: '100%',
      maxWidth: '450px',
      textAlign: 'center',
      backdropFilter: 'blur(10px)',
    };

    const buttonStyle = {
      marginTop: '20px',
      padding: '12px 20px',
      borderRadius: '50px',
      fontWeight: 'bold',
      transition: 'all 0.3s',
      backgroundColor: '#3f51b5',
      color: '#fff',
      boxShadow: '0px 4px 15px rgba(63, 81, 181, 0.5)',
    };

    const googleButtonStyle = {
      marginTop: '20px',
      padding: '12px 20px',
      borderRadius: '50px',
      fontWeight: 'bold',
      transition: 'all 0.3s',
      backgroundColor: '#4285F4',
      color: '#fff',
      boxShadow: '0px 4px 15px rgba(66, 133, 244, 0.5)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
    };

    const textFieldStyle = {
      marginBottom: '20px',
    };

    return (
      <div style={backgroundStyle}>
        <Container maxWidth="sm">
          <div style={formStyle}>
            <Typography variant="h4" gutterBottom style={{ fontFamily: "'Montserrat', sans-serif", fontWeight: 'bold' }}>
              Login
            </Typography>
            <TextField
              variant="outlined"
              type="text"
              autoComplete="off"
              name="username"
              value={this.state.username}
              onChange={this.onChange}
              placeholder="User Name"
              required
              fullWidth
              style={textFieldStyle}
            />
            <TextField
              variant="outlined"
              type="password"
              autoComplete="off"
              name="password"
              value={this.state.password}
              onChange={this.onChange}
              placeholder="Password"
              required
              fullWidth
              style={textFieldStyle}
            />
            <Button
              variant="contained"
              size="large"
              disabled={this.state.username === '' || this.state.password === ''}
              onClick={this.login}
              fullWidth
              style={buttonStyle}
              onMouseEnter={(e) => {
                e.target.style.backgroundColor = '#303f9f';
                e.target.style.boxShadow = '0px 6px 20px rgba(63, 81, 181, 0.7)';
              }}
              onMouseLeave={(e) => {
                e.target.style.backgroundColor = '#3f51b5';
                e.target.style.boxShadow = '0px 4px 15px rgba(63, 81, 181, 0.5)';
              }}
            >
              Login
            </Button>
            <Button
              variant="contained"
              size="large"
              onClick={() => {
                // Google Sign-In logic
              }}
              fullWidth
              style={googleButtonStyle}
              startIcon={<GoogleIcon />}
              onMouseEnter={(e) => {
                e.target.style.backgroundColor = '#357ae8';
                e.target.style.boxShadow = '0px 6px 20px rgba(66, 133, 244, 0.7)';
              }}
              onMouseLeave={(e) => {
                e.target.style.backgroundColor = '#4285F4';
                e.target.style.boxShadow = '0px 4px 15px rgba(66, 133, 244, 0.5)';
              }}
            >
              Sign in with Google
            </Button>
            <Link
              component="button"
              onClick={() => {
                this.props.navigate("/register");
              }}
              style={{ marginTop: '20px', color: '#3f51b5', fontWeight: 'bold' }}
            >
              Register
            </Link>
          </div>
        </Container>
        <Header />
      </div>
    );
  }
}

export default withRouter(Login);
