import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "./Header.css";
import moonIcon from "./assets/moon.svg";
import sunIcon from "./assets/sun.svg"; // assuming you have sun.svg for light mode

const Header = ({ isLoggedIn, onLogout }) => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [darkMode, setDarkMode] = useState(
    localStorage.getItem("darkMode") === "enabled"
  );

  const handleMenuToggle = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const toggleDarkMode = () => {
    if (darkMode) {
      document.body.classList.remove("dark__mode");
      localStorage.setItem("darkMode", "disabled");
    } else {
      document.body.classList.add("dark__mode");
      localStorage.setItem("darkMode", "enabled");
    }
    setDarkMode(!darkMode);
  };

  useEffect(() => {
    if (darkMode) {
      document.body.classList.add("dark__mode");
    }
  }, [darkMode]);

  return (
    <header className="header">
      <div className="container">
        <div className="logo">
          <Link to="/" className="navbar-branding">
            <img className="logo-img" src="assets/logoipsum-245.svg" alt="Logo" />
            <span className="brandstyle">MYMEDI <span className="logo-highlight">RECORDS</span></span>
          </Link>
        </div>
        <nav className={`nav ${isMenuOpen ? "open" : ""}`}>
          <Link to="/how-it-works" className="nav-link" onClick={handleMenuToggle}>How It Works</Link>
          <Link to="/get-smart-record" className="nav-link" onClick={handleMenuToggle}>Get Smart Record</Link>
          <Link to="/second-opinion" className="nav-link" onClick={handleMenuToggle}>Second Opinion</Link>
          {isLoggedIn ? (
            <>
              <Link to="/dashboard" className="nav-link" onClick={handleMenuToggle}>Dashboard</Link>
              <Link to="/" onClick={() => { onLogout(); handleMenuToggle(); }} className="nav-link">Logout</Link>
            </>
          ) : (
            <>
              <Link to="/login" className="nav-link" onClick={handleMenuToggle}>Sign In</Link>
              <Link to="/register" className="nav-link" onClick={handleMenuToggle}>Sign Up</Link>
            </>
          )}
        </nav>
        <div className="mode__con">
          <img
            className="lightswitch"
            src={darkMode ? sunIcon : moonIcon}
            alt={darkMode ? "sun icon" : "moon icon"}
            onClick={toggleDarkMode}
          />
        </div>
        <button className="menu-button" onClick={handleMenuToggle}>
          â˜°
        </button>
      </div>
    </header>
  );
};

export default Header;
