import React, { useState, useEffect, useRef } from 'react';
import '../Styles/App.css';
import WhatWeDo from '../Components/WhatWeDoSection';
import HealthAccount from '../Components/HealthAccount';
import UploadOptions from '../Components/UploadOptions';
import About from '../Components/About';
import WhyMediRecord from '../Components/WhyMediRecord';
import Footer from '../Components/Footer';
import Reviews from '../Components/Reviews';
import LandingPage from '../Components/LandingPage';
import Header from '../Header';

function App() {
  const [showCard1, setShowCard1] = useState(true);
  const whatWeDoRef = useRef(null);

  useEffect(() => {
    const timer = setInterval(() => {
      setShowCard1(prevState => !prevState);
    }, 5000);

    return () => clearInterval(timer);
  }, []);

  const scrollToWhatWeDo = () => {
    whatWeDoRef.current.scrollIntoView({ behavior: 'smooth' });
  };

  return (
    <div>
      <Header />
      <div className="app">
        <div className="left-section">
          <h1 className="font-montserrat">Ask Doctors for second opinion</h1>
          <p className="font-poppins">Your trusted platform for managing all your Personal Health records with ease.</p>
        </div>
      </div>

      <HealthAccount />
      <UploadOptions />
      <About />
      <LandingPage />
      <WhyMediRecord />
      <Reviews />
      <Footer />
    </div>
  );
}

export default App;
