import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { FaUser, FaHome, FaChartBar, FaFileUpload, FaSignOutAlt, FaBars } from 'react-icons/fa';
import swal from 'sweetalert';
import styled, { createGlobalStyle } from 'styled-components';
import ReportPage from './ReportPage';

const GlobalStyle = createGlobalStyle`
  body {
    margin: 0;
    font-family: Arial, sans-serif;
    background-color: #f5f7fa;
  }
`;

const DashboardContainer = styled.div`
  display: flex;
  transition: all 0.3s ease;
`;

const Sidebar = styled.div`
  width: ${props => (props.open ? '200px' : '60px')};
  height: 100vh;
  background-color: #2C3E50;
  color: white;
  display: flex;
  flex-direction: column;
  align-items: center;
  transition: width 0.3s ease;
`;

const SidebarToggle = styled.button`
  display: flex;
  align-items: center;
  background: none;
  border: none;
  cursor: pointer;
  padding: 10px;
  color: white;
  width: 100%;
  justify-content: space-between;

  &:hover {
    background-color: #34495E;
  }

  span {
    display: ${props => (props.open ? 'inline-block' : 'none')};
    color: #3498db;
    font-weight: bold;
    font-size: 130%;
    margin-right: 20px;
  }

  @media (max-width: 768px) {
    span {
      display: none;
    }
  }
`;

const SidebarMenu = styled.div`
  display: flex;
  flex-direction: column;
  margin-top: 20px;
  width: 100%;
`;

const SidebarMenuItem = styled.a`
  text-decoration: none;
  color: white;
  padding: 10px;
  display: flex;
  align-items: center;
  justify-content: ${props => (props.open ? 'left' : 'center')};
  width: 100%;
  transition: background 0.3s ease;

  &:hover {
    background-color: #34495E;
  }

  span {
    margin-left: 10px;
    display: ${props => (props.open ? 'inline-block' : 'none')};
  }
`;

const MainContent = styled.div`
  flex-grow: 1;
  background-color: #e2eaec;
  min-height: 100vh;
  display: flex;
  flex-direction: column;
`;

const TopBar = styled.div`
  background-color: #2980B9;
  color: white;
  padding: 10px 20px;
  display: flex;
  justify-content: flex-end;
  align-items: center;
`;

const ProfileIcon = styled.div`
  font-size: 24px;
  cursor: pointer;
`;

const Content = styled.div`
  padding: 20px;
  background-color: #e2eaec;
  flex-grow: 1;
`;

const GraphDashboardContainer = styled.div`
  width: 100%;
  height: 600px;
  margin-top: 20px;
`;

const ProfileSection = styled.div`
  .profile-section {
    display: flex;
    flex-direction: column;
    align-items: flex-start;
    padding: 20px;
    background-color: #fff;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
    border-radius: 10px;
  }

  h2 {
    margin-bottom: 20px;
  }

  p {
    margin: 5px 0;
  }

  button {
    margin-top: 20px;
  }
`;

const Profile = ({ user }) => {
  const [showChangePassword, setShowChangePassword] = useState(false);
  const [newPassword, setNewPassword] = useState('');

  const handleChangePassword = () => {
    console.log('Changing password to:', newPassword);
  };

  return (
    <ProfileSection>
      <h2>User Profile</h2>
      <p><strong>Username:</strong> {user.username}</p>
      <p><strong>Password:</strong> {user.password}</p>
      <p><strong>Name:</strong> {user.name}</p>
      <p><strong>email:</strong> {user.email}</p>

      <button onClick={() => setShowChangePassword(!showChangePassword)}>Change Password</button>
      {showChangePassword && (
        <div>
          <input type="password" placeholder="New Password" value={newPassword} onChange={(e) => setNewPassword(e.target.value)} />
          <button onClick={handleChangePassword}>Save</button>
        </div>
      )}
    </ProfileSection>
  );
};

const Graph = () => {
  return (
    <GraphDashboardContainer>
      <iframe
        src="http://54.84.119.128:3000/public/dashboard/8e9ae68b-140e-4f7e-97b9-f02af96e0c61?category=&testname="
        width="100%"
        height="100%"
        title="Graph Dashboard"
      ></iframe>
    </GraphDashboardContainer>
  );
};

const HomeDashboard = () => {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [user, setUser] = useState({});
  const [loading, setLoading] = useState(false);
  const [activeSection, setActiveSection] = useState('home');
  const navigate = useNavigate();

  useEffect(() => {
    getUserInfo();
  }, []);

  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen);
  };

  const getUserInfo = () => {
    const token = localStorage.getItem('token');
    setLoading(true);

    axios.get('http://localhost:2000/get-user-info', {
      headers: {
        'token': token
      }
    })
    .then((res) => {
      setUser(res.data.user);
      setLoading(false);
    })
    .catch((err) => {
      setLoading(false);
      swal({
        text: err.response?.data?.errorMessage || "An error occurred",
        icon: "error",
        type: "error"
      });
      navigate('/login');
    });
  };

  const handleSectionChange = (section) => {
    setActiveSection(section);
  };

  return (
    <>
      <GlobalStyle />
      <DashboardContainer className={sidebarOpen ? 'sidebar-open' : ''}>
        <Sidebar open={sidebarOpen}>
          <SidebarToggle onClick={toggleSidebar} open={sidebarOpen}>
            <span>MYMEDIRECORDS</span>
            <FaBars />
          </SidebarToggle>
          <SidebarMenu>
            <SidebarMenuItem href="#home" onClick={() => handleSectionChange('home')} open={sidebarOpen}>
              <FaHome />
              <span>Home</span>
            </SidebarMenuItem>
            <SidebarMenuItem href="#mymedirecords" open={sidebarOpen}>
              <FaHome />
              <span>MyMediRecords</span>
            </SidebarMenuItem>
            <SidebarMenuItem href="#graph" onClick={() => handleSectionChange('graph')} open={sidebarOpen}>
              <FaChartBar />
              <span>Graph</span>
            </SidebarMenuItem>
            <SidebarMenuItem href="#myrecords" open={sidebarOpen}>
              <FaFileUpload />
              <span>MyRecords</span>
            </SidebarMenuItem>
            <SidebarMenuItem href="#ReportPage" onClick={() => handleSectionChange('ReportPage')} open={sidebarOpen}>
              <FaFileUpload />
              <span>Upload Record</span>
            </SidebarMenuItem>
            <SidebarMenuItem href="/" open={sidebarOpen}>
              <FaSignOutAlt />
              <span>Logout</span>
            </SidebarMenuItem>
          </SidebarMenu>
        </Sidebar>
        <MainContent>
          <TopBar>
            <ProfileIcon>
              <a href="#profile" onClick={() => handleSectionChange('profile')}><FaUser /></a>
            </ProfileIcon>
          </TopBar>
          <Content>
            {loading ? (
              <p>Loading...</p>
            ) : (
              <>
                {activeSection === 'home' && (
                  <>
                    <h1>Welcome to MyMediRecords</h1>
                    <p>This is your dashboard.</p>
                  </>
                )}
                {activeSection === 'profile' && <Profile user={user} />}
                {activeSection === 'graph' && <Graph />}
                {activeSection === 'ReportPage' && <ReportPage />}
              </>
            )}
          </Content>
        </MainContent>
      </DashboardContainer>
    </>
  );
};

export default HomeDashboard;
