import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../Styles/HomeDashboard.css';
import { FaUser, FaHome, FaChartBar, FaFileUpload, FaSignOutAlt, FaBars } from 'react-icons/fa';
import swal from 'sweetalert';
import ReportPage from './ReportPage';

const Profile = ({ user }) => {
  const [showChangePassword, setShowChangePassword] = useState(false);
  const [newPassword, setNewPassword] = useState('');

  const handleChangePassword = () => {
    console.log('Changing password to:', newPassword);
  };

  return (
    <div className="profile-section">
      <h2>User Profile</h2>
      <p><strong>Username:</strong> {user.username}</p>
      <p><strong>Password:</strong> {user.password}</p>
      <button onClick={() => setShowChangePassword(!showChangePassword)}>Change Password</button>
      {showChangePassword && (
        <div>
          <input type="password" placeholder="New Password" value={newPassword} onChange={(e) => setNewPassword(e.target.value)} />
          <button onClick={handleChangePassword}>Save</button>
        </div>
      )}
    </div>
  );
};

const Graph = () => {
  return (
    <div className="graph-dashboard-container">
      <iframe
        src="http://54.84.119.128:3000/public/dashboard/8e9ae68b-140e-4f7e-97b9-f02af96e0c61?category=&testname="
        width="100%"
        height="100%"
        title="Graph Dashboard"
      ></iframe>
    </div>
  );
};

const HomeDashboard = () => {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [user, setUser] = useState({});
  const [loading, setLoading] = useState(false);
  const [activeSection, setActiveSection] = useState('home');
  const navigate = useNavigate(); // React Router's navigate function

  useEffect(() => {
    getUserInfo();
  }, []);

  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen);
  };

  const getUserInfo = () => {
    const token = localStorage.getItem('token');
    setLoading(true);

    axios.get('http://localhost:2000/get-user-info', {
      headers: {
        'token': token
      }
    })
    .then((res) => {
      setUser(res.data.user);
      setLoading(false);
    })
    .catch((err) => {
      setLoading(false);
      swal({
        text: err.response?.data?.errorMessage || "An error occurred",
        icon: "error",
        type: "error"
      });
      // Navigate to login page if user is not authenticated
      navigate('/login');
    });
  };

  const handleSectionChange = (section) => {
    setActiveSection(section);
  };

  return (
    <div className={`dashboard ${sidebarOpen ? 'sidebar-open' : ''}`}>
      <div className="sidebar">
        <button className="sidebar-toggle" onClick={toggleSidebar}>
          <span className="sidebar-toggle-text">MYMEDIRECORDS</span>
          <FaBars />
        </button>
        <div className="sidebar-menu">
          <a href="#home" onClick={() => handleSectionChange('home')}><FaHome /><span>Home</span></a>
          <a href="#mymedirecords"><FaHome /><span>MyMediRecords</span></a>
          <a href="#graph" onClick={() => handleSectionChange('graph')}><FaChartBar /><span>Graph</span></a>
          <a href="#myrecords"><FaFileUpload /><span>MyRecords</span></a>
          <a href="#ReportPage" onClick={() => handleSectionChange('ReportPage')}><FaFileUpload /><span>Upload Record</span></a>
          <a href="/"><FaSignOutAlt /><span>Logout</span></a>
        </div>
      </div>
      <div className="main-content">
        <div className="top-bar">
          <div className="profile-icon">
          <a href="#profile" onClick={() => handleSectionChange('profile')}><FaUser /></a>
          </div>
        </div>
        <div className="content">
          {loading ? (
            <p>Loading...</p>
          ) : (
            <>
              {activeSection === 'home' && (
                <>
                  <h1>Welcome to MyMediRecords</h1>
                  <p>This is your dashboard.</p>
                </>
              )}
              {activeSection === 'profile' && <Profile user={user} />}
              {activeSection === 'graph' && <Graph />}
              {activeSection === 'ReportPage' && <ReportPage />}
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default HomeDashboard;
