import React, { useState, useEffect } from 'react';
import axios from 'axios';
import swal from 'sweetalert';

const MyRecords = () => {
  const [records, setRecords] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch records from the backend when the component mounts
    fetchRecords();
  }, []);

  const fetchRecords = async () => {
    try {
      // Retrieve the token from local storage (assuming it's stored there)
      const token = localStorage.getItem('token');

      // Make a GET request to fetch records from the backend
      const response = await axios.get('http://localhost:2000/get-records', {
        headers: {
          Authorization: `Bearer ${token}` // Include the token in the request headers
        }
      });
      setRecords(response.data.records);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching records:', error);
      // Handle error with a sweet alert
      swal({
        text: 'Error fetching records. Please try again later.',
        icon: 'error',
      });
    }
  };

  return (
    <div>
      <h1>My Records</h1>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <div>
          {records.length > 0 ? (
            <ul>
              {records.map((record, index) => (
                <li key={index}>{record.filename}</li>
              ))}
            </ul>
          ) : (
            <p>No records found.</p>
          )}
        </div>
      )}
    </div>
  );
};

export default MyRecords;
