import React, { useState, useEffect } from 'react';
import axios from 'axios';
import swal from 'sweetalert';
import { Button, TextField, Dialog, DialogActions, DialogTitle, DialogContent, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, LinearProgress } from '@material-ui/core';
import Pagination from '@material-ui/lab/Pagination';

const UploadRecord = () => {
  const [file, setFile] = useState(null);
  const [description, setDescription] = useState('');
  const [uploading, setUploading] = useState(false);
  const [records, setRecords] = useState([]);
  const [page, setPage] = useState(1);
  const [pages, setPages] = useState(0);
  const [loading, setLoading] = useState(false);
  const [search, setSearch] = useState('');
  const [openEditModal, setOpenEditModal] = useState(false);
  const [currentRecord, setCurrentRecord] = useState(null);

  useEffect(() => {
    getRecords();
  }, [page, search]);

  const getRecords = () => {
    setLoading(true);
    let data = `?page=${page}`;
    if (search) {
      data += `&search=${search}`;
    }
    axios.get(`http://localhost:2000/get-records${data}`, {
  headers: {
    Authorization: `Bearer ${token}`, // Send JWT token in Authorization header
  },
})

      .catch(err => {
        setLoading(false);
        swal({
          text: err.response.data.errorMessage,
          icon: 'error',
        });
      });
  };

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleDescriptionChange = (e) => {
    setDescription(e.target.value);
  };

  const handleUpload = () => {
    if (!file || !description) {
      swal({
        text: 'Please provide a file and description.',
        icon: 'warning',
      });
      return;
    }

    const formData = new FormData();
    formData.append('file', file);
    formData.append('description', description);

    setUploading(true);

    axios.post('http://localhost:2000/upload-record', formData, {
      headers: {
        'Content-Type': 'multipart/form-data',
      },
    })
      .then((response) => {
        setUploading(false);
        swal({
          text: 'File uploaded successfully!',
          icon: 'success',
        });
        setFile(null);
        setDescription('');
        getRecords();
      })
      .catch((error) => {
        setUploading(false);
        swal({
          text: error.response?.data?.errorMessage || 'An error occurred',
          icon: 'error',
        });
      });
  };

  const handleEditOpen = (record) => {
    setCurrentRecord(record);
    setDescription(record.description);
    setOpenEditModal(true);
  };

  const handleEditClose = () => {
    setOpenEditModal(false);
    setCurrentRecord(null);
    setDescription('');
  };

  const handleUpdate = () => {
    const formData = new FormData();
    formData.append('id', currentRecord._id);
    if (file) formData.append('file', file);
    formData.append('description', description);

    axios.post('http://localhost:2000/update-record', formData, {
      headers: {
        'Content-Type': 'multipart/form-data',
      },
    })
      .then((res) => {
        swal({
          text: 'Record updated successfully!',
          icon: 'success',
        });
        handleEditClose();
        getRecords();
      })
      .catch((err) => {
        swal({
          text: err.response.data.errorMessage,
          icon: 'error',
        });
        handleEditClose();
      });
  };

  const handleDelete = (id) => {
    axios.post('http://localhost:2000/delete-record', { id }, {
      headers: {
        'Content-Type': 'application/json',
      },
    })
      .then((res) => {
        swal({
          text: 'Record deleted successfully!',
          icon: 'success',
        });
        getRecords();
      })
      .catch((err) => {
        swal({
          text: err.response.data.errorMessage,
          icon: 'error',
        });
      });
  };

  const handlePageChange = (event, value) => {
    setPage(value);
  };

  const handleSearchChange = (e) => {
    setSearch(e.target.value);
  };

  return (
    <div>
      {loading && <LinearProgress />}
      <h1>Upload Record</h1>
      <div>
        <input type="file" onChange={handleFileChange} />
        <input
          type="text"
          placeholder="Description"
          value={description}
          onChange={handleDescriptionChange}
        />
        <button onClick={handleUpload} disabled={uploading}>
          {uploading ? 'Uploading...' : 'Upload'}
        </button>
      </div>

      <Dialog
        open={openEditModal}
        onClose={handleEditClose}
      >
        <DialogTitle>Edit Record</DialogTitle>
        <DialogContent>
          <TextField
            type="text"
            autoComplete="off"
            value={description}
            onChange={handleDescriptionChange}
            placeholder="Description"
            required
          /><br />
          <Button variant="contained" component="label">
            Upload
            <input
              type="file"
              accept="image/*"
              onChange={handleFileChange}
              hidden
            />
          </Button>&nbsp;
          {file && file.name}
        </DialogContent>
        <DialogActions>
          <Button onClick={handleEditClose} color="primary">
            Cancel
          </Button>
          <Button onClick={handleUpdate} color="primary" autoFocus>
            Update
          </Button>
        </DialogActions>
      </Dialog>

      <TextField
        type="search"
        autoComplete="off"
        placeholder="Search by description"
        value={search}
        onChange={handleSearchChange}
      />

      <TableContainer>
        <Table aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell align="center">Description</TableCell>
              <TableCell align="center">File</TableCell>
              <TableCell align="center">Actions</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {records.map((record) => (
              <TableRow key={record._id}>
                <TableCell align="center">{record.description}</TableCell>
                <TableCell align="center">
                  <a href={`http://localhost:2000/${record.file}`} target="_blank" rel="noopener noreferrer">View File</a>
                </TableCell>
                <TableCell align="center">
                  <Button
                    variant="outlined"
                    color="primary"
                    size="small"
                    onClick={() => handleEditOpen(record)}
                  >
                    Edit
                  </Button>
                  <Button
                    variant="outlined"
                    color="secondary"
                    size="small"
                    onClick={() => handleDelete(record._id)}
                  >
                    Delete
                  </Button>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
        <Pagination count={pages} page={page} onChange={handlePageChange} color="primary" />
      </TableContainer>
    </div>
  );
};

export default UploadRecord;
