import React, { useState } from "react";
import { Link } from "react-router-dom";
import "./Header.css";

const Header = ({ isLoggedIn, onLogout }) => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const handleMenuToggle = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <div className="header">
      <div className="logo">
        <Link to="/">
          <p className="ft-title1">
            {/* <img src="/favicon.ico" alt="Favicon" className="favicon" /> Add favicon image */}
            MYMEDI <span className="ft-sign1">RECORDS</span>
          </p>
        </Link>
      </div>
      <button className="menu-button" onClick={handleMenuToggle}>
        â˜°
      </button>
      <div className={`nav-links ${isMenuOpen ? "open" : ""}`}>
        <Link to="/how-it-works" className="nav-link" onClick={handleMenuToggle}>How It Works</Link>
        <Link to="/get-smart-record" className="nav-link" onClick={handleMenuToggle}>Get Smart Record</Link>
        <Link to="/second-opinion" className="nav-link" onClick={handleMenuToggle}>Second Opinion</Link>
      </div>
      <div className={`auth-links ${isMenuOpen ? "open" : ""}`}>
        {isLoggedIn ? (
          <>
            <Link to="/dashboard" className="auth-link" onClick={handleMenuToggle}>Dashboard</Link>
            <span> | </span>
            <Link to="/" onClick={() => { onLogout(); handleMenuToggle(); }} className="auth-link">Logout</Link>
          </>
        ) : (
          <>
            <Link to="/login" className="auth-link" onClick={handleMenuToggle}>Sign In</Link>
            <span> | </span>
            <Link to="/register" className="auth-link1" onClick={handleMenuToggle}>Sign Up</Link>
          </>
        )}
      </div>
    </div>
  );
};

export default Header;
