import React, { useState, useEffect,useRef} from 'react';
import '../Styles/App.css';
import WhatWeDo from '../Components/WhatWeDoSection';
import HealthAccount from '../Components/HealthAccount';
import UploadOptions from '../Components/UploadOptions';
import About from '../Components/About';
import WhyMediRecord from '../Components/WhyMediRecord';
import Footer from '../Components/Footer';
import Reviews from '../Components/Reviews';
import LandingPage from '../Components/LandingPage';
import Header from '../Header';

function App() {
  const [currentSlide, setCurrentSlide] = useState(0);
  const whatWeDoRef = useRef(null);
  const slides = [
    {
      title: "Ask Doctors for second opinion",
      text: "Your trusted platform",
    },
    {
      title: "Trusted platform for managing all your Personal Health records with ease.",
      text: "All your Personal Health records with ease.",
    },
  ];

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentSlide((prevSlide) => (prevSlide + 1) % slides.length);
    }, 5000);

    return () => clearInterval(timer);
  }, [slides.length]);

  const nextSlide = () => {
    setCurrentSlide((prevSlide) => (prevSlide + 1) % slides.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prevSlide) => (prevSlide - 1 + slides.length) % slides.length);
  };

  return (
    <div>
      <Header />
      <div className="hero-container">
        {slides.map((slide, index) => (
          <div
            key={index}
            className={`slide ${index === currentSlide ? "active" : ""}`}
          >
            <h1 className="font-montserrat">{slide.title}</h1>
            <p className="font-poppins">{slide.text}</p>
          </div>
        ))}
        <button className="slide-button prev" onClick={prevSlide}>
          &#10094;
        </button>
        <button className="slide-button next" onClick={nextSlide}>
          &#10095;
        </button>
      </div>
      <div ref={whatWeDoRef}><WhatWeDo /></div>
      <HealthAccount />
      <UploadOptions />
      <About />
      <LandingPage />
      <WhyMediRecord />
      <Reviews />
      <Footer />
    </div>
  );
}

export default App;
