import React, { useState, useEffect, useRef } from 'react';
import '../Styles/App.css';
import WhatWeDo from '../Components/WhatWeDoSection';
import HealthAccount from '../Components/HealthAccount';
import UploadOptions from '../Components/UploadOptions';
import About from '../Components/About';
import WhyMediRecord from '../Components/WhyMediRecord';
import Footer from '../Components/Footer';
import Reviews from '../Components/Reviews';
import LandingPage from '../Components/LandingPage';
import Header from '../Header';

function App() {
  const [showCard1, setShowCard1] = useState(true);
  const whatWeDoRef = useRef(null);

  useEffect(() => {
    const timer = setInterval(() => {
      setShowCard1(prevState => !prevState);
    }, 5000);

    return () => clearInterval(timer);
  }, []);

  const scrollToWhatWeDo = () => {
    whatWeDoRef.current.scrollIntoView({ behavior: 'smooth' });
  };

  return (
    <div>
      <Header />
      <div className="app">
        <div className="left-section">
          <h1 className="font-montserrat">Open your health account</h1>
          <p className="font-poppins">Your trusted platform for managing all your Personal Health records with ease.</p>
          <div className="app-buttons">
            <button className="app-button font-poppins" onClick={scrollToWhatWeDo}>Click Here to Know More</button>
          </div>
          <div className="approvals font-poppins">
            <span>NHA Approved</span>
            <span>Co-WIN Approved</span>
            <span>Private & Secure</span>
          </div>
        </div>
        <div className="right-section">
          <div className={`card ${showCard1 ? 'carda' : 'cardb'} card-stepa`}>

          </div>
          <div className={`card ${showCard1 ? 'cardb' : 'carda'} card-stepb`}></div>
        </div>
      </div>
      <div ref={whatWeDoRef}><WhatWeDo /></div>
      <HealthAccount />
      <UploadOptions />
      <About />
      <LandingPage />
      <WhyMediRecord />
      <Reviews />
      <Footer />
    </div>
  );
}

export default App;
