import React, { Component } from "react";
import swal from "sweetalert";
import { Button, TextField, Link } from "@material-ui/core";
import { withRouter } from "../utils";
import '../Styles/Login.css'; // Import custom CSS file for login page styling
import Header from "../Header";
const axios = require("axios");
const bcrypt = require("bcryptjs");
var salt = bcrypt.genSaltSync(10);

class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      username: '',
      password: '',
      isGoogleScriptLoaded: false,
    };
  }

  componentDidMount() {
    // Load the Google Identity Services library
    this.loadGoogleScript();
  }

  loadGoogleScript = () => {
    const script = document.createElement('script');
    script.src = 'https://accounts.google.com/gsi/client';
    script.async = true;
    script.defer = true;
    script.onload = this.onGoogleScriptLoad;
    document.body.appendChild(script);
  }

  onGoogleScriptLoad = () => {
    this.setState({ isGoogleScriptLoaded: true });
    this.initializeGoogleSignIn();
  }

  initializeGoogleSignIn = () => {
    if (window.google) {
      window.google.accounts.id.initialize({
        client_id: 'YOUR_CLIENT_ID.apps.googleusercontent.com',
        callback: this.handleCredentialResponse,
      });
      window.google.accounts.id.renderButton(
        document.getElementById("google-signin-button"),
        { theme: "outline", size: "large" } // Customization attributes
      );
    }
  }

  handleCredentialResponse = (response) => {
    axios.post('http://localhost:2000/google-login', {
      id_token: response.credential
    }).then(res => {
      // Handle successful login
      localStorage.setItem('token', res.data.token);
      localStorage.setItem('user_id', res.data.id);
      localStorage.setItem('loggedIn', 'true'); // Set flag indicating user is logged in
      this.props.navigate("/HomeDashboard");
    }).catch(err => {
      console.error('Google Sign-In failed:', err);
      swal({
        text: 'Google Sign-In failed. Please try again.',
        icon: "error",
      });
    });
  }

  onChange = (e) => this.setState({ [e.target.name]: e.target.value });

  login = () => {
    const pwd = bcrypt.hashSync(this.state.password, salt);

    axios.post('http://localhost:2000/login', {
      username: this.state.username,
      password: pwd,
    }).then((res) => {
      localStorage.setItem('token', res.data.token);
      localStorage.setItem('user_id', res.data.id);
      localStorage.setItem('loggedIn', 'true'); // Set flag indicating user is logged in
      this.props.navigate("/HomeDashboard");
    }).catch((err) => {
      if (err.response && err.response.data && err.response.data.errorMessage) {
        swal({
          text: err.response.data.errorMessage,
          icon: "error",
        });
      }
    });
  }

  render() {
    return (
      <div className="login-container">
        <div className="login-form">
          <h2>Login</h2>
          <TextField
            id="standard-basic"
            type="text"
            autoComplete="off"
            name="username"
            value={this.state.username}
            onChange={this.onChange}
            placeholder="User Name"
            required
          />
          <TextField
            id="standard-basic"
            type="password"
            autoComplete="off"
            name="password"
            value={this.state.password}
            onChange={this.onChange}
            placeholder="Password"
            required
          />
          <Button
            className="button-style"
            variant="contained"
            color="primary"
            size="small"
            disabled={this.state.username === '' || this.state.password === ''}
            onClick={this.login}
          >
            Login
          </Button>
          <div id="google-signin-button"></div> {/* Placeholder for the Google Sign-In button */}
          <Link
            component="button"
            onClick={() => {
              this.props.navigate("/register");
            }}
          >
            Register
          </Link>
        </div>
        <Header />
      </div>
    );
  }
}

export default withRouter(Login);
