import React, { useState } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import Swal from 'sweetalert2';

import '../Styles/ReportPage.css';

const ReportPage = () => {
  const [startDate, setStartDate] = useState(new Date());
  const [endDate, setEndDate] = useState(new Date());
  const [records, setRecords] = useState([]);

  const handleSearch = () => {
    // Perform search operation here
    Swal.fire('Search clicked', 'Implement search logic here', 'info');
    setRecords([]); // Update this with actual search results
  };

  const handleReset = () => {
    setStartDate(new Date());
    setEndDate(new Date());
    setRecords([]);
  };

  const handleUpload = () => {
    // Implement upload functionality here
    Swal.fire('Upload clicked', 'Implement upload logic here', 'info');
  };

  return (
    <div className="report-page">
      <h2>Report</h2>
      <div className="report-header">
        <button className="btn search-btn" onClick={handleSearch}>Search</button>
        <button className="btn reset-btn" onClick={handleReset}>Reset</button>
        <button className="btn upload-btn" onClick={handleUpload}>Upload Report</button>
      </div>
      <div className="report-body">
        <p>Total {records.length} Record Found.</p>
        {records.length === 0 ? (
          <div className="no-report">
            <p>No Report Available.</p>
            <button className="btn upload-btn" onClick={handleUpload}>Upload Report</button>
          </div>
        ) : (
          // Render records here
          <div className="report-list">
            {/* Example of rendering records */}
            {records.map((record, index) => (
              <div key={index} className="report-item">
                {record}
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default ReportPage;
