import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom'; // Import Link from React Router
import '../Styles/App.css';
import WhatWeDo from '../Components/WhatWeDoSection';
import HealthAccount from '../Components/HealthAccount';
import UploadOptions from '../Components/UploadOptions';
import About from '../Components/About';
import WhyMediRecord from '../Components/WhyMediRecord';
import Footer from '../Components/Footer';
import Reviews from '../Components/Reviews';
import LandingPage from '../Components/LandingPage';
import Header from '../Header';

function App() {
  const [showCard1, setShowCard1] = useState(true);
  const whatWeDoRef = useRef(null);

  useEffect(() => {
    const timer = setTimeout(() => {
      setShowCard1(prevState => !prevState);
    }, 5000); // Change to 5000 milliseconds for 5 seconds

    return () => clearTimeout(timer);
  }, [showCard1]);

  const scrollToWhatWeDo = () => {
    whatWeDoRef.current.scrollIntoView({ behavior: 'smooth' });
  };

  return (

    <div>
      <div className="app">
        <div className="left-section">

          <h1 >Open your health account</h1>
          <p>Your trusted platform for managing all your Personal Health records with ease.</p>
          <div className="app-buttons">
            <button className="app-button" onClick={scrollToWhatWeDo}>Click Here to Know More</button>
          </div>
          <div className="approvals">
            <span>NHA Approved</span>
            <span>Co-WIN Approved</span>
            <span>Private & Secure</span>
          </div>
        </div>
        <div className="right-section">
          <div className="card1 card-step1">
            <h2>Start The Process</h2>
            <p>to get started with your Health Account</p>
            <Link to="/register"><button>Create Account</button></Link> {/* Use Link to navigate */}
          </div>
        </div>
        <div className="right-section">
          <div className="card1 card-step1">
            <h2>Start The Process</h2>
            <p>to get started with your Health Account</p>
            <Link to="/register"><button>Create Account</button></Link> {/* Use Link to navigate */}
          </div>
        </div>
      </div>
      <div ref={whatWeDoRef}><WhatWeDo /></div>
      <Header />
      <HealthAccount />
      <UploadOptions />
      <About />
      <LandingPage />
      <WhyMediRecord />
      <Reviews />
      <Footer />
    </div>
  );
}

export default App;
