import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { FaUser, FaHome, FaChartBar, FaFileUpload, FaSignOutAlt, FaBars, FaClipboardList } from 'react-icons/fa';
import swal from 'sweetalert';
import '../Styles/HomeDashboard.css';
import HomePage from './DashbordCompo/HomePage';
import MyMediRecords from './DashbordCompo/MyMediRecords';
import MyRecords from './DashbordCompo/MyRecords';
import UploadRecord from './DashbordCompo/UploadRecord';
import Profile from './DashbordCompo/Profile';
import Graph from './DashbordCompo/Graph';

const HomeDashboard = () => {
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [user, setUser] = useState({});
  const [loading, setLoading] = useState(false);
  const [activeSection, setActiveSection] = useState('home');
  const navigate = useNavigate();

  useEffect(() => {
    getUserInfo();
  }, []);

  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen);
  };

  const getUserInfo = () => {
    const token = localStorage.getItem('token');
    setLoading(true);

    axios.get('http://localhost:2000/get-user-info', {
      headers: {
        'token': token
      }
    })
    .then((res) => {
      setUser(res.data.user);
      setLoading(false);
    })
    .catch((err) => {
      setLoading(false);
      swal({
        text: err.response?.data?.errorMessage || "An error occurred",
        icon: "error",
        type: "error"
      });
      navigate('/login');
    });
  };

  const handleSectionChange = (section) => {
    setActiveSection(section);
    if (window.innerWidth <= 768) {
      setSidebarOpen(false);
    }
  };

  return (
    <div className={`dashboard-container ${sidebarOpen ? 'sidebar-open' : ''}`}>
      <div className={`sidebar ${sidebarOpen ? 'open' : ''}`}>
        <button className="sidebar-toggle" onClick={toggleSidebar}>
          <FaBars />
        </button>
        <div className="sidebar-menu">
          <div className="sidebar-header">
            <h2>MYMEDIRECORDS</h2>
          </div>
          <a className={`sidebar-menu-item ${activeSection === 'home' ? 'active' : ''}`} onClick={() => handleSectionChange('home')}>
            <FaHome />
            <span>Home</span>
          </a>
          <a className={`sidebar-menu-item ${activeSection === 'mymedirecords' ? 'active' : ''}`} onClick={() => handleSectionChange('mymedirecords')}>
            <FaClipboardList />
            <span>MyMediRecords</span>
          </a>
          <a className={`sidebar-menu-item ${activeSection === 'graph' ? 'active' : ''}`} onClick={() => handleSectionChange('graph')}>
            <FaChartBar />
            <span>Graph</span>
          </a>
          <a className={`sidebar-menu-item ${activeSection === 'myrecords' ? 'active' : ''}`} onClick={() => handleSectionChange('myrecords')}>
            <FaFileUpload />
            <span>MyRecords</span>
          </a>
          <a className={`sidebar-menu-item ${activeSection === 'uploadrecord' ? 'active' : ''}`} onClick={() => handleSectionChange('uploadrecord')}>
            <FaFileUpload />
            <span>Upload Record</span>
          </a>
          <a className="sidebar-menu-item" onClick={() => {
            localStorage.removeItem('token');
            navigate('/login');
          }}>
            <FaSignOutAlt />
            <span>Logout</span>
          </a>
        </div>
      </div>
      <div className="main-content">
        <div className="top-bar">
          <div className="profile-icon">
            <a onClick={() => handleSectionChange('profile')}><FaUser /></a>
          </div>
        </div>
        <div className="content">
          {loading ? (
            <div className="loading-spinner">
              <div className="spinner"></div>
              <p>Loading...</p>
            </div>
          ) : (
            <>
              {activeSection === 'home' && <HomePage />}
              {activeSection === 'mymedirecords' && <MyMediRecords />}
              {activeSection === 'graph' && <Graph />}
              {activeSection === 'myrecords' && <MyRecords />}
              {activeSection === 'uploadrecord' && <UploadRecord />}
              {activeSection === 'profile' && <Profile user={user} />}
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default HomeDashboard;
