import React, { Component } from "react";
import swal from "sweetalert";
import { Button, TextField, Link, Typography, Container } from "@material-ui/core";
import { withRouter } from "../utils";
import Header from "../Header";
import '../Styles/Register.css'; // Import custom CSS file for register page styling
const axios = require("axios");

class Register extends Component {
  constructor(props) {
    super(props);
    this.state = {
      username: '',
      email: '',
      password: '',
      confirm_password: ''
    };
  }

  onChange = (e) => this.setState({ [e.target.name]: e.target.value });

  register = () => {
    axios.post('http://localhost:2000/register', {
      username: this.state.username,
      password: this.state.password,
    }).then((res) => {
      swal({
        text: res.data.title,
        icon: "success",
        type: "success"
      });
      this.props.navigate("/");
    }).catch((err) => {
      swal({
        text: err.response.data.errorMessage,
        icon: "error",
        type: "error"
      });
    });
  }

  render() {
    return (
      <div className="backgroundStyle">
        <Container maxWidth="sm">
          <div className="formStyle">
            <Typography variant="h4" gutterBottom style={{ fontFamily: "'Montserrat', sans-serif", fontWeight: 'bold' }}>
              Register
            </Typography>
            <TextField
              variant="outlined"
              type="text"
              autoComplete="off"
              name="username"
              value={this.state.username}
              onChange={this.onChange}
              placeholder="User Name"
              required
              fullWidth
              className="textFieldStyle"
            />
            <TextField
              variant="outlined"
              type="password"
              autoComplete="off"
              name="password"
              value={this.state.password}
              onChange={this.onChange}
              placeholder="Password"
              required
              fullWidth
              className="textFieldStyle"
            />
            <TextField
              variant="outlined"
              type="password"
              autoComplete="off"
              name="confirm_password"
              value={this.state.confirm_password}
              onChange={this.onChange}
              placeholder="Confirm Password"
              required
              fullWidth
              className="textFieldStyle"
            />
            <Button
              variant="contained"
              size="large"
              disabled={this.state.username === '' || this.state.password === ''}
              onClick={this.register}
              fullWidth
              className="buttonStyle"
            >
              Register
            </Button>
            <Link
              component="button"
              onClick={() => {
                this.props.navigate("/Login");
              }}
              className="loginLink"
            >
              Login
            </Link>
          </div>
        </Container>
        <Header />
      </div>
    );
  }
}

export default withRouter(Register);
