import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { FaUser, FaHome, FaChartBar, FaFileUpload, FaSignOutAlt, FaBars } from 'react-icons/fa';
import swal from 'sweetalert';
import '../Styles/HomeDashboard.css';
import HomePage from './DashbordCompo/HomePage';
import MyMediRecords from './DashbordCompo/MyMediRecords';
import MyRecords from './DashbordCompo/MyRecords';
import UploadRecord from './DashbordCompo/UploadRecord';
import Profile from './DashbordCompo/Profile';
import Graph from './DashbordCompo/Graph';

const HomeDashboard = () => {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [user, setUser] = useState({});
  const [loading, setLoading] = useState(false);
  const [activeSection, setActiveSection] = useState('home');
  const navigate = useNavigate();

  useEffect(() => {
    getUserInfo();
  }, []);

  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen);
  };

  const getUserInfo = () => {
    const token = localStorage.getItem('token');
    setLoading(true);

    axios.get('http://localhost:2000/get-user-info', {
      headers: {
        'token': token
      }
    })
    .then((res) => {
      setUser(res.data.user);
      setLoading(false);
    })
    .catch((err) => {
      setLoading(false);
      swal({
        text: err.response?.data?.errorMessage || "An error occurred",
        icon: "error",
        type: "error"
      });
      navigate('/login');
    });
  };

  const handleSectionChange = (section) => {
    setActiveSection(section);
  };

  return (
    <>
      <div className={`dashboard-container ${sidebarOpen ? 'sidebar-open' : ''}`}>
        <div className={`sidebar ${sidebarOpen ? 'open' : ''}`}>
          <button className="sidebar-toggle" onClick={toggleSidebar}>
            <span>MYMEDIRECORDS</span>
            <FaBars />
          </button>
          <div className="sidebar-menu">
            <a className={`sidebar-menu-item ${activeSection === 'home' ? 'active' : ''}`} href="#home" onClick={() => handleSectionChange('home')}>
              <FaHome />
              <span>Home</span>
            </a>
            <a className={`sidebar-menu-item ${activeSection === 'mymedirecords' ? 'active' : ''}`} href="#mymedirecords" onClick={() => handleSectionChange('mymedirecords')}>
              <FaHome />
              <span>MyMediRecords</span>
            </a>
            <a className={`sidebar-menu-item ${activeSection === 'graph' ? 'active' : ''}`} href="#graph" onClick={() => handleSectionChange('graph')}>
              <FaChartBar />
              <span>Graph</span>
            </a>
            <a className={`sidebar-menu-item ${activeSection === 'myrecords' ? 'active' : ''}`} href="#myrecords" onClick={() => handleSectionChange('myrecords')}>
              <FaFileUpload />
              <span>MyRecords</span>
            </a>
            <a className={`sidebar-menu-item ${activeSection === 'uploadrecord' ? 'active' : ''}`} href="#uploadrecord" onClick={() => handleSectionChange('uploadrecord')}>
              <FaFileUpload />
              <span>Upload Record</span>
            </a>
            <a className="sidebar-menu-item" href="/" onClick={() => localStorage.removeItem('token')}>
              <FaSignOutAlt />
              <span>Logout</span>
            </a>
          </div>
        </div>
        <div className="main-content">
          <div className="top-bar">
            <div className="profile-icon">
              <a href="#profile" onClick={() => handleSectionChange('profile')}><FaUser /></a>
            </div>
          </div>
          <div className="content">
            {loading ? (
              <p>Loading...</p>
            ) : (
              <>
                {activeSection === 'home' && <HomePage />}
                {activeSection === 'mymedirecords' && <MyMediRecords />}
                {activeSection === 'graph' && <Graph />}
                {activeSection === 'myrecords' && <MyRecords />}
                {activeSection === 'uploadrecord' && <UploadRecord />}
                {activeSection === 'profile' && <Profile user={user} />}
              </>
            )}
          </div>
        </div>
      </div>
    </>
  );
};

export default HomeDashboard;
