import React, { Component } from "react";
import swal from "sweetalert";
import { Button, TextField, Link } from "@material-ui/core";
import { withRouter } from "../utils";
import Header from "../Header";
import '../Styles/Register.css'; // Import custom CSS file for register page styling
const axios = require("axios");

class Register extends Component {
  constructor(props) {
    super(props);
    this.state = {
      username: '',
      email: '',
      password: '',
      confirm_password: ''
    };
  }

  onChange = (e) => this.setState({ [e.target.name]: e.target.value });

  register = () => {
    axios.post('http://localhost:2000/register', {
      username: this.state.username,
      password: this.state.password,
    }).then((res) => {
      swal({
        text: res.data.title,
        icon: "success",
        type: "success"
      });
      this.props.navigate("/");
    }).catch((err) => {
      swal({
        text: err.response.data.errorMessage,
        icon: "error",
        type: "error"
      });
    });
  }

  render() {
    const backgroundStyle = {
      backgroundImage: 'url("https://i.postimg.cc/KYXFX1pB/Pngtree-background-biru-keren-dan-kosong-1738466.png")',
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      backgroundRepeat: 'no-repeat',
      height: '100vh',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
    };

    const formStyle = {
      background: 'rgba(255, 255, 255, 0.8)',
      padding: '20px',
      borderRadius: '10px',
      boxShadow: '0px 4px 10px rgba(0, 0, 0, 0.1)',
      width: '100%',
      maxWidth: '400px',
      textAlign: 'center',
    };

    return (
      <div style={backgroundStyle}>
        <div style={formStyle}>
          <h2>Register</h2>
          <TextField
            id="standard-basic"
            type="text"
            autoComplete="off"
            name="username"
            value={this.state.username}
            onChange={this.onChange}
            placeholder="User Name"
            required
            fullWidth
          />
          <TextField
            id="standard-basic"
            type="password"
            autoComplete="off"
            name="password"
            value={this.state.password}
            onChange={this.onChange}
            placeholder="Password"
            required
            fullWidth
          />
          <TextField
            id="standard-basic"
            type="password"
            autoComplete="off"
            name="confirm_password"
            value={this.state.confirm_password}
            onChange={this.onChange}
            placeholder="Confirm Password"
            required
            fullWidth
          />
          <Button
            className="button-style"
            variant="contained"
            color="primary"
            size="large"
            disabled={this.state.username === '' || this.state.password === ''}
            onClick={this.register}
            fullWidth
            style={{ marginTop: '20px' }}
          >
            Register
          </Button>
          <Link
            className="login-link"
            component="button"
            onClick={() => {
              this.props.navigate("/Login");
            }}
            style={{ marginTop: '10px', color: '#3f51b5', fontWeight: 'bold' }}
          >
            Login
          </Link>
        </div>
        <Header />
      </div>
    );
  }
}

export default withRouter(Register);
